import {BsPlus} from 'react-icons/bs';
const tambah = () =>{
    return(
        <div className=" flex justify-center">
            <button className="bg-primary  rounded-2xl">
            <TopIcon icon={<BsPlus size="38"/>} />
            </button>
        </div>
    )
}
const TopIcon = ({icon, text = "Add your own message!"} ) =>{ // menambahkan component untuk icon
    return(
        <div className="flex  relative  items-center justify-center h-12 w-12 mt-2 mb-2 shadow-lg bg-gray-800 rounded-3xl transform  text-green-500 hover:bg-green-600 hover:text-white mx-auto transition-all duration-150 ease-linear  group">
            {icon}
            <span className="absolute w-auto p-2 m-2  min-w-max left-14 rounded-md shadow-md text-white bg-gray-800 text-xs transform font-bold transition-all duration-100 scale-0  origin-left group-hover:scale-100">
                {text}
            </span>
        </div>
    )
}
export default tambah;
