import {BsPlus} from 'react-icons/bs';
const tambah = () =>{
    return(
        <div className=" flex justify-center">
            <button className="bg-primary  rounded-2xl">
            <TopIcon icon={<BsPlus size="38"/>} />
            </button>
        </div>
    )
}
const TopIcon = ({icon, text = 'Add your own message!'} ) =>{ // menambahkan component untuk icon
    return(
        <div className="relative flex  items-center justify-center h-12 w-12 mx-auto transform mt-2 mb-2 group" >
            {icon}

            <span className="absolute w-auto p-2 m-2 min-w-max top-14 rounded-md shadow-md transform text-white hover:scale-100 bg-gray-900 text-xs scale-0 origin-top">
                {text}
            </span>
        </div>
    )
}
export default tambah;
