import { useState } from 'react';
const Tambah = ({store}) =>{
    
    const [inputQuote, setInputQuote] = useState();


    function handleQuote(event) {
        setInputQuote({...inputQuote, quote: event.target.value});
    }
    function handleAuthor(event) {
        setInputQuote({...inputQuote, by: event.target.value});
    }
    function submitAdd(event){
        
        event.preventDefault();
        store(inputQuote)
    }
    return(
    <div id="formtambah " className="bg-secondary  w-full h-screen   text-center ">
        
        <form className="form-row grid place-items-center  text-xl font-hammer text-center" onSubmit={submitAdd}> 
        <div>
              <label for="about" class="block text-xl font-medium text-gray-700">
                About
              </label>
              <div class="mt-1">
                <textarea id="about" name="about" rows="3" class="shadow-sm focus:ring-indigo-500 focus:border-indigo-500 mt-1 block w-full sm:text-sm border border-gray-300 rounded-md" placeholder="you@example.com"></textarea>
              </div>
              <p class="mt-2 text-sm text-gray-500">
                Brief description for your profile. URLs are hyperlinked.
              </p>
            </div>
            <div className="grid place-items-stretch text-center relative">
                <input type="text" name="your" className="peer border-b focus:outline-none relate w-96 h-12 bg-transparent text-black my-2"  onChange={handleAuthor}/>
                <label for="messages" className="absolute top-1  peer-focus:-top-4">Your name</label>

            </div>
            <div className="grid place-items-stretch text-center text-2xl ease-linear duration-100 ">
                <input type="submit" className=" my-2 w-72  rounded-lg  text-center cursor-pointer  bg-primary" value="Submit"/>
            </div>
        </form>
    </div>
    )
}

export default Tambah;
