import {BsPlus} from 'react-icons/bs';
import React from 'react';
import Tippy from '@tippyjs/react';
import 'tippy.js/dist/tippy.css'; // optional
const tambah = () =>{
    return(
        <div className=" flex justify-center">
            <button className="bg-primary  rounded-2xl">
            <TopIcon icon={<BsPlus size="38"/>} />
            </button>
            <StringContent icon={<BsPlus size="38"/>}/>
            <JSXContent/>
        </div>
    )
}
const TopIcon = ({icon, text = 'Add your own message!'} ) =>{ // menambahkan component untuk icon
    return(
        <div className="relative flex   items-center justify-center h-12 w-12 mx-auto  mt-2 mb-2 group" >
            {icon}
            <span className="absolute  w-auto p-2 m-2 min-w-max bottom-14 rounded-md shadow-md  text-white hover:scale-100 bg-gray-900 text-xs scale-0 origin-top">
                {text}
            </span>

        </div>
    )
}

const StringContent = ({icon}) => (
    <Tippy content="Hello">
      <button>{icon}</button>
    </Tippy>
  );
  
  const JSXContent = () => (
    <Tippy content={<span>Tooltip</span>}>
      <button>My button</button>
    </Tippy>
  );
export default tambah;
