import { useState } from 'react';
const Tambah = ({store}) =>{
    
    const [inputQuote, setInputQuote] = useState();


    function handleQuote(event) {
        setInputQuote({...inputQuote, quote: event.target.value});
    }
    function handleAuthor(event) {
        setInputQuote({...inputQuote, by: event.target.value});
    }
    function submitAdd(event){
        
        event.preventDefault();
        store(inputQuote)
    }
    return(
    <div id="formtambah " className="bg-secondary  w-full h-screen   text-center ">
        <h5 className="text-white text-3xl mt-32 grid place-items-center font-hammer">
            Add message
        </h5>
        <form className="form-row grid place-items-center  text-xl font-hammer text-center" onSubmit={submitAdd}> 
            <div className="grid  place-items-stretch text-center relative">
                <textarea type="text" name="quote" className=" border-b focus:outline-none w-96 h-48 text-black bg-gray-500 my-2" placeholder="write your message here" onChange={handleQuote}/>
                <label className="">Your message</label>
            </div>
            <div className="grid place-items-stretch text-center">
                <input type="text" name="your" className=" text-center w-96 h-12 bg-gray-500 text-black my-2" placeholder="your name" onChange={handleAuthor}/>
            </div>
            <div className="grid place-items-stretch text-center text-2xl ease-linear duration-100 ">
                <input type="submit" className=" my-2 w-72  rounded-lg  text-center cursor-pointer  bg-primary" value="Submit"/>
            </div>
        </form>
    </div>
    )
}

export default Tambah;
